<template>
	<view>
		<u-navbar back-text="返回" title="登录" :background="background" :customBack="backIndex"></u-navbar>
		<u-modal v-model="showBack" @confirm="confirm" ref="uModal" :async-close="true" :show-cancel-button="true"
			content="确认退出？"></u-modal>
		<u-top-tips ref="uTips"></u-top-tips>
		<u-image width="100%" height="300rpx"
			src="data:image/png;base64,">
		</u-image>
		
		<view class="login-card">
			<view class="box">
				<uni-title type="h1" align="center" title="欢迎登录云订餐"></uni-title>
			</view>
			<u-row gutter="16">
				<u-col span="1">
					<view class="demo-layout bg-purple"></view>
				</u-col>
				<u-col span="10">
					<view>
						<u-form :model="loginForm" ref="loginForm">
							<u-form-item>
								<u-field style="margin-right: 10rpx;" v-model="loginForm.phone" label="+86 |" label-width=80 placeholder="请输入手机号" focus/>
								<view>
									<u-verification-code :seconds="60" ref="uCode" @change="codeChange">
									</u-verification-code>
									<u-button type="warning" size="mini" @click="getCheckCode">{{tips}}</u-button>
								</view>
							</u-form-item>
							<u-form-item>
								<view @click="showKeyboard = !showKeyboard" v-show="showCodeMessageBox">
									<u-message-input :maxlength="6" v-model="loginForm.checkCode" :disabled-keyboard="true"
										:breathe="true" font-size="50" width="50" :focus="true">
									</u-message-input>
								</view>
							</u-form-item>
						</u-form>
					</view>
				</u-col>
				<u-col span="1">
					<view class="demo-layout bg-purple"></view>
				</u-col>
			</u-row>
			<view class="u-flex u-row-center u-m-t-25">
				<u-button @click="toLogin" type="warning" size="medium">登录</u-button>
			</view>
		</view>
		<u-toast ref="uToast" position="bottom" />
		<u-keyboard @change="keyboardChange" @backspace="keyboardBackspace" ref="uKeyboard" mode="number"
			v-model="showKeyboard" :mask="false" :dot-enabled="false" :show-tips="false" :cancelBtn="false"
			:confirmBtn="false"></u-keyboard>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				background: {
					// 渐变色
					backgroundImage: 'linear-gradient( 135deg, #FDEB71 10%, #F8D800 100%)'
				},
				showKeyboard: false,
				showCodeMessageBox: true,
				showBack: false,
				loginForm: {
					phone: '',
					checkCode: ''
				},
				tips: '',
				fieldStyle: {
					"background": "#aaaaaa"
				}
			}
		},
		methods: {
			// 按键被点击(点击退格键不会触发此事件)
			keyboardChange(val) {
				// 将每次按键的值拼接到checkCode变量中，注意+=写法
				if (this.loginForm.checkCode.length < 6) {
					this.loginForm.checkCode += val
				}
			},
			// 退格键被点击
			keyboardBackspace() {
				// 删除checkCode的最后一个字符
				if (this.loginForm.checkCode.length) {
					this.loginForm.checkCode = this.loginForm.checkCode.substr(0, this.loginForm.checkCode.length - 1)
				}
				console.log(this.loginForm.checkCode);
			},
			// 获取验证码
			async getCheckCode() {
				if (this.$u.test.isEmpty(this.loginForm.phone)) {
					this.$refs.uToast.show({
						title: '手机号码不能为空',
						type: 'default',
					})
				} else {
					if (this.$u.test.mobile(this.loginForm.phone)) {
						// 发送验证码
						if (this.$refs.uCode.canGetCode) {
							// 模拟向后端请求验证码
							uni.showLoading({
								title: '正在获取验证码'
							})
							const result = await this.$u.api.sendCode(this.loginForm.phone)
							uni.hideLoading()
							// 这里此提示会被this.start()方法中的提示覆盖
							this.$u.toast('验证码已发送')
							// this.showCodeMessageBox = true
							// 通知验证码组件内部开始倒计时
							this.$refs.uCode.start()
						} else {
							this.$u.toast('请在倒计时结束后再发送~')
						}
					} else {
						this.$refs.uToast.show({
							title: '手机号码格式错误',
							type: 'default',
						})
					}
				}
			},
			codeChange(text) {
				this.tips = text
			},
			async toLogin() {
				if (this.loginForm.checkCode.length < 6) {
					this.$refs.uToast.show({
						title: '请输入验证码',
						type: 'default',
					})
				} else {
					const result = await this.$u.api.login({
						phone: this.loginForm.phone,
						code: this.loginForm.checkCode,
						password: "",
						isVendor: 1
					})
					this.$u.vuex('vuex_merchant_token', result)
					console.log(this.vuex_merchant_token)
					this.$u.route({
						type: 'switchTab',
						url: 'pages/index/index'
					})
				}
			},
			backIndex() {
				this.showBack = true
			},
			toHome() {
				this.$u.route({
					type: 'switchTab',
					url: 'pages/index/index'
				})
			},
			confirm() {
				this.showBack = false;
				this.toHome()
			}
		},
		// 必须要在onReady生命周期，因为onLoad生命周期组件可能尚未创建完毕
		onReady() {
			this.$refs.uTips.show({
				title: '请先登录哦~',
				type: 'error',
				duration: '2300'
			})

			this.$refs.loginForm.setRules(this.rules)
		}
	}
</script>

<style scoped lang="scss">
	.box {
		display: block;
	}

	.demo-layout {
		height: 80rpx;
		border-radius: 8rpx;
	}

	.bg-purple {
		background: #d3dce6;
		opacity: 0;
	}
	
	.login-card {
		width: 92%;
		height: 730rpx;
		background-color: #ffffff;
		border-radius: 35rpx;
		margin: 30rpx;
		padding: 10rpx;
	}
</style>
